<?php

/*
Plugin Name: Email Obfuscation
Description: Hides email addresses in the HTML code.
Author: JÃ¶rn Lund
Author URI: http://github.org/mcguffin
Version: 0.0.1
*/

class EmailObfuscator {
	private $map_fragments = array();
	private static $_instance = null;
	
	/**
	 * Getting a singleton.
	 *
	 * @return object single instance of SteinPostTypeJob
	 */
	public static function instance() {
		if ( is_null( self::$_instance ) )
			self::$_instance = new self();
		return self::$_instance;
	}

	/**
	 * Private constructor
	 */
	private function __construct() {
		$this->cachepath = WP_CONTENT_DIR . DIRECTORY_SEPARATOR . 'cache'. DIRECTORY_SEPARATOR .'email-obfuscation';
		if ( ! is_admin() ) {
			add_action( 'wp_head' , array( &$this, 'enqueue_script' ) );
			add_action( 'template_redirect' , array( &$this , 'start_output' ) , 1 );
			add_action( 'shutdown' , array( &$this , 'store_fragments' ) , 99 , 0 );
		} else {
			
		}
		add_action( 'wp_ajax_get_email_fragments' , array( &$this , 'ajax_get_fragments' ) );
		add_action( 'wp_ajax_nopriv_get_email_fragments' , array( &$this , 'ajax_get_fragments' ) );
	}
	
	function enqueue_script() {	
		wp_register_script( 'email-obfuscation' , plugins_url( 'email-obfuscation.js' , __FILE__ ) , array( 'jquery' )  );
		wp_localize_script( 'email-obfuscation' , 'email_obfuscator' , array(
			'ajax_url' => admin_url('admin-ajax.php'),
		) );
		wp_enqueue_script( 'email-obfuscation' );
	}
	function ajax_get_fragments( ) {
		$file = $this->_get_cache_file( $_SERVER['HTTP_REFERER'] );
		header('Content-Type: application/json');
		if ( file_exists( $file ) )
			echo file_get_contents( $file );
		exit();
	}
	function store_fragments() {
		if ( ! wp_mkdir_p( $this->cachepath ) )
			wp_die('Can\'t write to cache dir');
		
		file_put_contents( $this->_get_cache_file() , json_encode( $this->map_fragments ) );
	}

	function start_output( ) {
		ob_start( array( &$this , 'filter_output' ) );
	}
	
	
	function filter_output( $output ) {
		if ( empty( $output ) )
			return '';
		// source: http://www.w3.org/TR/html5/forms.html#valid-e-mail-address
		$pattern_link = '/<a([^>]*)href=([\'"]{1})mailto:.+\/a>/imsU';
		$pattern_email = "/[a-zA-Z0-9\.!#$%&'*+\/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*/s";
		$output = preg_replace_callback( $pattern_link , array( &$this , '_replace_email_link' ) , $output );
		$output = preg_replace_callback( $pattern_email , array( &$this , '_replace_email_address' ) , $output );
		return $output;
	}
	
	private function _replace_email_link( $matches ) {
		$hash = md5($matches[0]);
		$this->map_fragments[$hash] = $matches[0];
		return sprintf( '<a href="#%s" data-load-fragment="%s">%s</a>' , $hash , $hash , __('(Click to show Email Link)' , 'stein-agency-mu-plugins' ) );
	}
	private function _replace_email_address( $matches ) {
		$hash = md5($matches[0]);
		$this->map_fragments[$hash] = $matches[0];
		return sprintf( '<a href="#%s" data-load-fragment="%s">%s</a>' , $hash , $hash , __('(Click to show Email Address)' , 'stein-agency-mu-plugins' ) );
	}
	

	private function _get_cache_file( $url = '' ) {
		return $this->cachepath . DIRECTORY_SEPARATOR . $this->_get_hash( $url ) . '.json';
	}
	
	private function _get_hash( $url = '' ) {
		// make sure to get rid of the port part in $_SERVER['HTTP_HOST']
		$hash_url = empty($url) ? ( parse_url('http://'.$_SERVER['HTTP_HOST'],PHP_URL_HOST) . $_SERVER['REQUEST_URI'] ) : ( parse_url($url, PHP_URL_HOST) . parse_url($url, PHP_URL_PATH) );
		return md5( $hash_url . NONCE_SALT );
	}
	
	
	function install() {
		if ( ! wp_mkdir_p( $this->cachepath ) )
			wp_die('Cannot write to cache dir');
		$htaccess = $this->cachepath . DIRECTORY_SEPARATOR.'.htaccess';
		if ( ! file_exists( $htaccess ) && ! file_put_contents( $htaccess , 'deny from all'."\n" ) )
			wp_die('Cannot create .htaccess in cache dir');
	}
}

EmailObfuscator::instance();

